// Fonction qui prend une référence à une string en markdown et renvoie une string en html
std::string format_text(std::string_view text, std::string_view title) {
    std::string_view markdown = text;
    std::string_view html;

    // Traitement du markdown
    auto markdown_split = markdown.split("\n");
    for (auto it = markdown_split.begin(); it != markdown_split.end(); ++it) {
        if (*it != "---") {
            html += markdown.substr(*it);
        } else {
            html += std::string{"<div>"} + std::string{"</div>"}
                + markdown.substr(*it)
                + std::string{"</div>"}
                + std::string{"</article>"}
                + std::string{"</div>"}
                + title;
        }
    }

    return html;
}
```

