import NextAnalyzerSentiment from "next-analyzer-sentiment";

export default function App() {
  const [userReview, setUserReview] = useState("");
  const handleSubmit = e => {
    e.preventDefault();
    setUserReview(userReview);
  };

  return (
    <div className="App">
      <h1>Evento, demostración o noticia?</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="userReview">Revisa tu opinión sobre el evento:</label>
        <input
          type="text"
          id="userReview"
          value={userReview}
          onChange={handleSubmit}
          required
        />
        <button type="submit">Sé el primero en dar su opinión</button>
      </form>
      <p>
        {userReview}
        <br />
        <span>
          {
            userReview
              ?
                `Es un evento.`
              :
                `Es una demostración.`
              :
              `Es una noticia.`
          }
        </span>
      </p>
    </div>
  );
}

