def buscar(array, valor):
    inicio = 0
    for i in range(len(array)):
        if array[i] == valor:
            inicio = i
            break
    return inicio

def buscar_binaria(array, valor):
    if len(array) <= 1:
        return array[0]
    if valor == array[0]:
        return buscar_binaria(array[1:], valor)
    elif array[0] < valor:
        return buscar_binaria(array[1:], array[0])
    else:
        return buscar_binaria(array[1:], buscar_binaria(array[0:], valor))


