function isPrime(numArray) {
    // Declarar una variable que almacene el último número del array
    var lastNumber = numArray[numArray.length - 1];

    // Declarar una variable que almacene el producto de todos los números del array
    var productOfAllNumbers = numArray[0] * numArray[1] * numArray[2] * numArray[3] * numArray[4];

    // Verificar si el producto de todos los números del array es divisible por todos los números del array
    if (productOfAllNumbers > lastNumber) {
        return false;
    }

    // Declarar una variable que almacene el último número del array
    var lastNumberInProduct = productOfAllNumbers % lastNumber;

    // Verificar si el último número del array es divisible por todos los números del array
    if (lastNumberInProduct !== 0) {
        return false;
    }

    // Declarar una variable que almacene el último número del array
    var lastNumberInModuloSquared = lastNumberInProduct ** 2;

    // Verificar si el último número del array es divisible por todos los números del array
    if (lastNumberInModuloSquared !== lastNumber) {
        return false;
    }

    return true;
}

