std::string html_from_markdown(const std::string &markdown)
{
    // Parse the markdown text
    std::stringstream stream;
    stream << markdown;

    // Convert the HTML to HTML elements
    std::string html = stream.str();
    std::vector<std::string> html_elements = MarkdownToHTML(html);

    // Convert the HTML elements back to a string
    std::string result;
    for (const auto &html_element : html_elements)
    {
        result += html_element;
    }

    return result;
}
