    public static void main(String[] args) {
        int[] firstArray = {1, 2, 3};
        int[] secondArray = {1, 4, 5};
        int[] commonElements = new int[firstArray.length];

        for (int i = 0; i < firstArray.length; i++) {
            commonElements[i] = firstArray[i] + secondArray[i];
        }

