std::string markdownToHTML(const std::string& markdown) {
    std::string html;

    // HTML markup for paragraphs
    html += "<p>";

    // HTML markup for lists
    html += "<ul>";

    // Loop through each paragraph in the markdown string and convert it to HTML
    for (const char* p = markdown.begin(); p != markdown.end(); ++p) {
        // If this character is a newline, then we have ended the paragraph and start a new one
        if (*p == '\n') {
            html += "<br>";

            // Otherwise, add the character to the paragraph's HTML
            html += "<p>" + std::string((char) *p);
        } else if (*p == '<') {
            // Open a list item
            html += "<li>";
        } else if (*p == '>') {
            // Close a list item
            html += "</li>";
        } else if (*p == '"') {
            // Open an inline quote
            html += std::string((char) p[1]);
        } else if (*p == '`') {
            // Open a code block
            html += std::string((char) p[1]);
        } else if (*p == p[0]){
            // Normal text
            html.push_back(*p);
        }
    }

    // Close the list
    html.push_back('</ul>');

    // Close the paragraph
    html.push_back('</p>');

    return html;
}
```

