```cpp
std::string MarkdownToHtml(const std::string& markdownText)
{
    // Crée un objet HTML avec les attributs `html`, `head`, et `body`
    std::stringstream ss;

    // Débute la balise <html>
    ss << "<html lang=\"en\">";

    // Ajoute la balise <head>
    ss << "<head><meta charset=\"utf-8\">";

    // Ajoute la balise <body>
    ss << "<body>";

    // Ajoute le texte de la balise <h1>
    if (!markdownText.empty())
    {
        ss << "<h1>" << markdownText << "</h1>";
    }

    // Ajoute le texte de la balise <p>
    if (!markdownText.empty())
    {
        ss << "<p>" << markdownText << "</p>";
    }

    // Ajoute le texte de la balise <div>
    if (!markdownText.empty())
    {
        ss << "<div>" << markdownText << "</div>";
    }

    // Ajoute le texte de la balise <a href=\"/url\">Afficher l\'URL</a>
    if (!markdownText.empty())
    {
        ss << "<a href=\"/url\">Afficher l\'URL</a>";
    }

    // Ajoute le texte de la balise <br/> au besoin
    if(markdownText.empty()&&!ss.str().empty())
    {
        ss.str("");
        ss.clear();
    }

    // Termine la balise body avec un saut de ligne et ajoute le texte de la balise html
    ss.str("");
    ss.clear();

    ss.put("\n");

    return ss.str();
}
