def timsort(list: list_of_ints):
  def sort_by_freq(seq: list_of_ints):
    sorted_seq = seq.empty();
    for elem in seq:
      if elem not in sorted_seq:
        sorted_seq.push(elem);
      else:
        sorted_seq.insert(elem, sorted_seq.count(elem));
    return sorted_seq;
