            return "";
        for (int i=0; i<nums1.length; i++) {
            if (nums1[i]!=nums2[i])
                continue;
            else
                return getCommonString(nums1,i,nums2);
        }
        return null;
    }

    private String getCommonString(int[] nums1, int index, int[] nums2) {
        for (int i=index; i<nums1.length; i++)
            if (nums1[i]==nums2[i])
                return getCommonString(nums1,i+1,nums2);
            else
                return null;
    }

    private String getCommonString(int[] nums1, int index, int[] nums2, int[] common) {
        if (common[0]==null)
            common[0]=getCommonString(nums1,index,nums2);
        if (common[1]==null)
            common[1]=getCommonString(nums1,index+common[0].length-1,nums2);
        return common[0]+common[1];
    }

    public static void main(String[] args) {
        int[] nums1={3,4,5};
        int[] nums2={7,8};

        System.out.println(new Solution().findCommonElements(nums1,nums2));
    }
