import java.math.BigInteger;

public boolean isPowerOfThree(int n) {
    if (n == 0)
        return false;
    BigInteger x = new BigInteger(String.valueOf(3) + "^" + String.valueOf(n));
    return x.isProbablePrime() && n == x.probablePrimeFactors().length;
}

