#include <algorithm>
#include <vector>

template <typename T>
class Timsort {
public:
    Timsort(const std::vector<T>& arr) {
        if (arr.size() <= 1) return;

        auto left_size = arr.size() / 2;
        auto right_size = arr.size() - left_size;

        std::vector<T> left(left_size);
        std::vector<T> right(right_size);

        for (auto i = 0; i < left_size; i++)
            left[i] = arr[i];

        for (auto i = left_size; i < arr.size(); i++)
            left[i] = arr[i];

        for (auto i = right_size; i < arr.size(); i++)
            right[i] = arr[i];

        std::vector<T> tmp;

        for (auto j = left_size; j >= right_size; j--)
            tmp.push_back(left[j]);

        for (auto j = right_size; j >= left_size; j--)
            tmp.push_back(right[j]);

        std::swap(left, right);
    }

    void sort() const
    {
        auto begin  = left.begin();
        auto end    = left.end();
        auto end1   = right.begin();
        auto end2   = right.end();
        for (auto itr1  = begin, itr2  = end1; itr2  != end2;)
        {
            if ((itr2  - itr1)  % sizeof(T)    == sizeof(T))
            {
                std::swap(*itr1,*itr2);
                itr1    += sizeof(T);
                itr2    -= sizeof(T);
            }
            else
            {
                break;
            }
        }
    }
};

