from queue import PriorityQueue

@lru_cache(maxsize=None)
def sorted_list(l: List[int]) -> List[int]:
    k = 10  # Taille des groupes
    pq: PriorityQueue[List[int]] = PriorityQueue(maxsize=None)  # Queue des groupes
    for x in l:
        k -= 1
        if k < 0:  # Fin de groupe
            if pq and len(pq) > 0:
