#include <stdlib.h>
#include <math.h>

int main(void) {
    // Array für den Pfad
    int board[9][9] = {
        {1,2,3},
        {4,5,6},
        {7,8,9}
    };

    // Die Position der startPosition
    int startPosition = 2;

    // Werden die Position mit der aktuellen Position überschrieben
    for (int i = 0; i < 9; i++)
        for (int j = 0; j < 9; j++)
            if (board[startPosition][j] == startPosition)
                board[startPosition][j]--;

    // Werden alle Leere Positionen für das Spiel ausgelöscht
