const binarySearch = (arr, elment, inicial) => {
  if (arr.length === 0) return null

  let midpoint = (arr.length - 1) / 2
  const midpointValue = arr[midpoint]

  if (elment === midpointValue) return midpoint

  const leftElement = arr.slice(0, midpoint)
  const rightElement = arr.slice(midpoint + 1, arr.length)

  if (elment < leftElement[0]) return binarySearch(leftElement, elment, inicial)
  if (elment > rightElement[0]) return binarySearch(rightElement, elment, inicial)

  return binarySearch(leftElement, elment, inicial)
    ? midpoint
    : binarySearch(rightElement, elment, inicial)
    ? midpoint
    : null
