function isValidPrime(numbers) {
  // Check for empty array
  if (!Array.isArray(numbers)) {
    return false;
  }

  // Check for less than 2 elements
  if (numbers.length < 2) {
    return false;
  }

  // Check for all elements are numbers
  if (typeof numbers[0] !== 'number') {
    return false;
  }

  // Check if the numbers is a prime number
  if (!numbers.every((n) => isPrime(n))) {
    return false;
  }

  // Get the largest number in the array
  const maxNum = Math.max(...numbers);

  // Loop through all of the numbers and find the largest prime factor
  for (let i = maxNum; i >= numbers[0]; i--) {
    // If the number has any factors that are smaller than itself
    // it is not a prime number
    if (numbers[i] % i) {
      return false;
    }
  }

  return true;
}
```

