def longestCommonSubsequence(str1: str, str2: str) -> str:
    n = len(str1)
    m = len(str2)
    if n == 0 or m == 0:
        return ''

    str1_words = list(filter(None, map(lambda x: x.isalpha(), str1)))
    str2_words = list(filter(None, map(lambda x: x.isalpha(), str2)))

    str1_words_lengths = [len(x) for x in str1_words]
    str2_words_lengths = [len(x) for x in str2_words]

    dp = list()

    for i in range(n):
        for j in range(m):
            if i < n or j < m:
                continue
            if dp and dp[-1][i] + dp[-1][j] + 1 not in str1_words_lengths:
                continue
            elif i and j and not dp and dp[-1][i] + dp[-1][j] not in str2_words_lengths:
                continue
            elif i and j and not dp and dp[-1][i] not in str2_words_lengths:
                continue
            elif i and j and not dp and i+j not in str1_words_lengths:
                continue

            if len(dp) < n+m-i-j+1:
                dp.append([i, j])
                break

    return max([x[::-1] for x in sorted(dp)])
