    if (array.length === 0) {
        return false;
    }
    let sum = 0;
    for (let i = 0; i < array.length; i++) {
        sum += array[i];
    }
    if (sum % array[0] === 0) return false;
    let currentSum = sum;
    for (let i = array.length-1; i>=0; i--) {
