import java.util.Scanner;
public class PowerOfThree {
    public static boolean isPowerOfThree(int n) {
        if (n < 0 || n > 231 - 1) {
            return false;
        }
        int i = 0;
        while (i < n) {
            if (n % 3 == 0) {
                return true;
            }
            i++;
        }
        return false;
    }
}
```

