function isPrime(arr) {
    for (let i = 0; i < arr.length; i++) {
        if (arr[i] === 1 || arr[i] === 0) {
            continue;
        }
        let sum = 0;
        for (let j = 2; j <= Math.floor(arr[i]/2); j++) {
            sum += arr[i] % j; // checking if the number is divisible by j
        }
        if (sum === 0) {
            return true; // the number is prime
        } else {
            return false; // the number is not prime
        }
    }
    return false; // no prime numbers found
}
```
