def lcs(seq1, seq2):
    """Returns the longest common subsequence between two sequences."""
    if len(seq1) == 0 or len(seq2) == 0:
        return None
    else:
        res = [None] * len(seq2)
        for i in range(len(seq1)):
            for j in range(i + 1, len(seq1)):
