```c++
void convert_markdown_to_html(const std::string& markdown_text, std::string& html_text) {
    std::regex regex_pattern("```([^```]*)```");
    std::smatch match;

    // Replaces the Markdown code blocks with HTML code blocks.
    for (auto it = markdown_text.begin(); it != markdown_text.end(); it++) {
        if (std::regex_match(it->first, match, regex_pattern)) {
            // We only need to replace the block of Markdown text in between the ``` and ```.
            html_text.insert(it, std::regex_replace(markdown_text.begin(), it, match[0], "<code>"));
            html_text.erase(match[1]);
        } else {
            // Any other character should be left as is.
        }
    }
}
```

