    // Split the markdown text into tokens using the Markdown library
    std::vector<std::string> tokens;
    std::regex markdownRegex;
    markdownRegex = std::regex("(?:.*?\\n)+");
    std::smatch match;
    for (auto const& line : markdownString) {
