void markdownToHtml(std::string &markdownText, std::string &htmlText) {
    // Tokenize the Markdown text into an array of tokens
    std::vector<std::string> tokens;
    split(markdownText, "```", tokens);

    // Replace the backticks in each token with HTML entities
    for (auto token : tokens) {
        replace(token.begin(), token.end(), '`' , "&lt;b>`");
        replace(token.begin(), token.end(), "'", "&lt;i>`");
        replace(token.begin(), token.end(), '"', "&lt;q>`");
        replace(token.begin(), token.end(), "<", "&lt;");
