using System;

public class FibonacciProgram {
    public static int GetFibonacciNumber(int n) {
        if (n <= 1)
            return 1;
        int a = 0, b = 1;
        int nthFibonacci = 0;
        for (int i = 2; i < n + 2; i++) {
