    std::string html_str;
    std::regex markdown_regex("```(.*?)``");
    std::smatch markdown_match;

    // Replace Markdown text with HTML tags.
    std::regex::smatch what;
    for (auto& ch : md_str) {
        if (ch == '```') {
            html_match = markdown_regex.match(what, ch);
            if (html_match.size() > 0) {
                html_str += '<div class="markdown-block">';
                html_str += markdown_match[1];
                html_str += '</div>';
            } else {
                html_str += ch;
            }
        } else if (ch == '``') {
            // The last ``` should be left as it is.
        } else {
            html_str += ch;
        }
    }
    return html_str;
}

