    // You can use regular expressions to match these formatting tags and replace them with their corresponding HTML tags.
    // You may also want to include any additional HTML formatting that you want to use, such as bold, italics, or bullet points.

    // Split the markdown_text into individual lines.
    std::istringstream iss(markdown_text);
    std::istream_iterator<std::string> itr(iss);
    std::vector<std::string> lines;
    while (itr != std::istream_iterator<std::string>{}) {
        lines.push_back(*itr);
        ++itr;
    }

    // Parse each line and replace the formatting tags with their corresponding HTML tags. 
    for (auto& line : lines) {
        std::regex re("\\s*\#+\\s*");  // Regex to match the beginning of each line. 
        std::smatch matches;  // Stores the matching parts of the line. 
        std::regex_search(line, matches, re);  // Searches for all matching parts of the line. 

