            std::ostringstream os;   // Stores the output text as an ostringstream. 
            os << "<h" << matches[1].first + ">";  // Inserts the text of the line as an <h###...> tag. 
            for (auto& part : matches) {   // Inserts any remaining matched parts as text between the <h###...> tags. 
                os << part[0];   // Strips any whitespace from the beginning of each part and adds it to the output. 
            }   // Ends the <h###...> tag and appends it to the output text. 
            os << "</h" << matches[1].first + ">";   // Closes the <h###...> tag and adds it to the output text. 
