  console.log(`Logged in as ${client.user.username}#${client.user.discriminator}`); // Logs "Logged in as Client#0000" to the console when the bot is ready
});

client.on("message", message => { // Listen to messages from the message channel
  if (!message.guild) return; // Only process messages from guild channels (since we want to use slash commands)
  if (!message.content.startsWith(/``)) return; // Only process commands that start with a backtick (since we only want slash commands)
  try { // Try to parse the command and dispatch the event accordingly:
    const { name, args } = message.content.split(" "); // Split the message content into its constituent parts: "command" and "arguments" (where the backtick is) are in name and args respectively
    const command = new Discord.ApplicationCommand(name); // Instantiate a new ApplicationCommand for this command
    command.slashCommand(client, message, args); // Dispatch the event to the bot with its arguments as parameters
  } catch (e) { // Catch any errors that may occur while parsing the command:
    console.error(e); // Log the error to the console
  }
});

