```c++
#include <iostream>
using namespace std;

int main() {
    string nom_mot;
    string ligne;
    string fichier_texte;
    int contient_nom_mot = 0;

    cout << "Numéro de ligne à compter: ";
    cin >> ligne;
    ifstream ficheur(ligne); // ouverture du fichier texte
    while (ficheur >> ligne) { // lecture du fichier ligne par ligne
        if (ligne.find(nom_mot) != nom_mot.size()) continue; // si la ligne n'a pas contenu le mot spécifique, continuez la lecture des autres lignes
        contient_nom_mot++; // ajouter 1 à la comptabilité du nombre d'occurrences du mot spécifique dans le fichier texte
    }
    cout << "Le nombre d'occurrences de " << nom_mot << " dans le fichier est " << contient_nom_mot << endl;
}
