```c++
#include <iostream>
#include <fstream>
using namespace std;

int main() {
    ifstream file("input.txt"); // open text file
    string word; // store word to count instances of
    int count = 0; // count number of instances of word
    while (file >> word) { // read line from file
        if (word == "hello") { // if word is "hello"
            count++; // increment count by 1
        }
    }
    cout << "Found " << count << " instances of \"hello\"."; // print number of instances of word to output file
    return 0; // return 0 to indicate successful program execution
}
