use lean_core::prelude::*;

pub fn timsort<T: Ord>(array: &mut [T]) {
    if array.is_empty() { return; }

    let mut len = array.len();
    let mut i = 0;
    while i < len / 2 {
        swap(&mut array[i], &mut array[len - 1 - i]);
        len -= 1;
    }
    if len % 2 != 0 {
        swap(&mut array[len - 1], &mut array[len - 2]);
    }
}
