from queue import LifoQueue, Queue

class LifoQueue(Queue):
    def __init__(self, size: int) -> None:
        super().__init__(size)
        self.l_q = LifoQueue(self.max_size)
        self.r_q = LifoQueue(self.max_size)

    def put(self, item: object) -> None:
        self.l_q.put(item)
        if self.l_q.empty():
            self.r_q.put(item)

    def get(self) -> object:
        return self.l_q.get() if not self.l_q.empty() else self.r_q.get()

    def size(self) -> int:
        return self.l_q.size() + self.r_q.size()

    def empty(self) -> bool:
        return self.l_q.empty() or self.r_q.empty()
