def find_common_sequence(a, b):
    # Initialize variables for our common sequence
    common_sequence = ""
    len_a = len(a)
    len_b = len(b)

    # Loop through the two sequences
    for i in range(len_a):
        for j in range(len_b):
            if a[i] == b[j]:
                # If the two characters match, add to the common sequence
                common_sequence += a[i]

    # Return the length of the common sequence
    return len(common_sequence)

