def BusquedaBinaria(listaOrdenada: Sequence[int], valorBuscado: int):
    if (listaOrdenada.length == 0):
        return -1
    if (listaOrdenada[0] == valorBuscado):
        return 0
    if (listaOrdenada[0] > valorBuscado):
