def longestCommonSubsequence(input1, input2):
    """
    Returns the length of the longest common subsequence between input1 and input2.
    """
    if not input1 or not input2:
        return 0

    if len(input1) == 0 or len(input2) == 0:
        return 0

    result = 0
    N = len(input1) + len(input2)
    dp_matrix = [[None for _ in range(N)] for _ in range(N)]
    for i in range(len(input1)):
        for j in range(len(input2)):
            if input1[i] == input2[j]:
                dp_matrix[i][j] = dp_matrix[i-1][j-1] + 1
            else:
                dp_matrix[i][j] = max(dp_matrix[i-1][j], dp_matrix[i][j-1]) + 1
        result = max(result, dp_matrix[-1][-1])  # find longest common subsequence in reverse order and return its length.
    return result

