public class Solution {
    private static boolean isPowerOfThree(int n) {
        HashMap<Integer, Boolean> dictionary = new HashMap<Integer, Boolean>();
        while (true) {
            int key = n;
            dictionary.put(key, false);
            if (dictionary.containsKey(key)) continue;
            while (key <= n) {
                if (n % key == 0) {
                    dictionary.put(key, true);
                    key *= key;
                } else break;
            }
            if (dictionary.containsKey(key)) break;
        }
        return dictionary.containsKey(n);
    }
}
```

