def longest_common_subsequence(s1, s2):
    # Crea una matriu per a emmagatzemar els valors de la solució
    matrix = [[0] * (len(s2) + 1) for _ in range(len(s1) + 1)]

    # Inicialitza els valors de la matriu en 0
    for i in range(len(s1) + 1):
        matrix[i][0] = 0
    for j in range(len(s2) + 1):
        matrix[0][j] = 0

    # Itera sobre les cadenes d'entrada per trobar la subseqüència comuna més llarga
    for i in range(1, len(s1) + 1):
        for j in range(1, len(s2) + 1):
            if s1[i - 1] == s2[j - 1]:
                matrix[i][j] = matrix[i - 1][j - 1] + 1
            else:
                matrix[i][j] = max(matrix[i - 1][j], matrix[i][j - 1])

    # Troba la subseqüència comuna més llarga a partir de la matriu i torna-la
    result = []
    i, j = len(s1), len(s2)
    while i > 0 and j > 0:
        if s1[i - 1] == s2[j - 1]:
            result.append(s1[i - 1])
            i -= 1
            j -= 1
        elif matrix[i - 1][j] > matrix[i][j - 1]:
            i -= 1
        else:
            j -= 1
    result.reverse()
    return "".join(result)

