def longest_common_subsequence(str1, str2):
    m = len(str1)
    n = len(str2)
    result = [["" for _ in range(n + 1)] for _ in range(m + 1)]

    for i in range(m + 1):
        for j in range(n + 1):
            if str1[i - 1] == str2[j - 1]:
                result[i][j] = result[i - 1][j - 1] + str1[i - 1]
            else:
                result[i][j] = max(result[i - 1][j], result[i][j - 1], key=len)
    return result[m][n]
