    int count = 0;
    int three = 3;
    while (n != 1) {
        if (n % three == 0) {
            count++;
            n /= three;
        } else {
            return false;
        }
    }

    // If count is even, n is a power of three, otherwise it's not
    return count % 2 == 0;
}
