def longest_common_subsequence(seq1, seq2):
    n = len(seq1)
    m = len(seq2)
    dp = [[0 for _ in range(m+1)] for _ in range(n+1)]
    max_length = 0
    for i in range(n+1):
        for j in range(m+1):
            if i == 0 or j == 0:
                dp[i][j] = 0
            elif seq1[i-1] == seq2[j-1]:
                dp[i][j] = dp[i-1][j-1] + 1
                if dp[i][j] > max_length:
                    max_length = dp[i][j]
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])
    return [seq1[i - max_length + 1:i + 1] for i in range(n) if dp[i][m] == max_length]
