class TwoStackQueue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, item):
        self.stack1.append(item)

    def dequeue(self):
        if len(self.stack2) == 0:
            while len(self.stack1) > 0:
                item = self.stack1.pop()
                self.stack2.append(item)
        return self.stack2.pop()
