def longest_common_subsequence(text1, text2):
    # 定义一个长度为 text1 长度 + text2 长度 的列表，用于存储结果
    result = []

    # 初始化动态规划矩阵
    dp = [[0 for i in range(len(text1)+1)] for j in range(len(text2)+1)]

    # 递推公式：从最后一行开始，每一行的每一个元素都是以下几个值中的最大值：
    # 当前元素位置和前一行同位置的元素是不同字符时的值
    # 当前元素位置和前一行同位置的元素是相同字符时的值加上前一行相同字符对应位置的值
    for i in range(len(text2)):
        for j in range(len(text1)):
            if text2[i] == text1[j]:
                dp[i+1][j+1] = dp[i][j] + 1
            else:
                dp[i+1][j+1] = max(dp[i][j+1], dp[i+1][j])

    # 从矩阵最后一行开始，反向查找最长公共子序列
    i, j = len(text2), len(text1)
    while i > 0 and j > 0:
        if text2[i-1] == text1[j-1]:
            result.append(text2[i-1])
            i -= 1
            j -= 1
        elif dp[i-1][j] > dp[i][j-1]:
            i -= 1
        else:
            j -= 1

    # 反转结果序列，返回最长公共子序列
    return result[::-1]

