def longest_common_subsequence(text1, text2):
    len_text1, len_text2 = len(text1), len(text2)
    # 동적 프로그래밍 테이블 선언
    lcs_table = [[0 for _ in range(len_text2 + 1)] for _ in range(len_text1 + 1)]
    # 테이블에서 각 요소의 가장 긴 공통 부분 수열을 구하기 위해 backtracking을 수행합니다.
    for i in range(len_text1):
        for j in range(len_text2):
            if text1[i] == text2[j]:
                lcs_table[i + 1][j + 1] = lcs_table[i][j] + 1
            else:
