    if (!interaction.isCommand()) return;

    if (interaction.commandName === "ban") {
        if (!interaction.memberPermissions.has("BAN_MEMBERS")) return interaction.reply("Du har ikke rettigheder til at banne medlemmer.");
        if (!interaction.guild.me.permissions.has("BAN_MEMBERS")) return interaction.reply("Jeg har ikke rettigheder til at banne medlemmer.");
