        if (!user) return interaction.reply(`Kan ikke finde brugeren "${user}" i serveren.`);
        const reason = interaction.options.getString("reason");
        await interaction.guild.members.ban(user, { reason: reason || "Ingen angivet." });
        await interaction.reply(`Banede brugeren ${user}.`);
    }
});

client.login(token);
