def longest_common_subsequence(seq1, seq2):
    # Luo LCS-matriisi
    n = len(seq1) + 1
    m = len(seq2) + 1
    LCS_matrix = [[0 for i in range(m)] for j in range(n)]

    # Määrittele matriisin ensimmäinen rivi ja sarake nollaksi, sillä ne eivät kuulu LCS:hon
    for i in range(n):
        LCS_matrix[i][0] = 0
    for i in range(m):
        LCS_matrix[0][i] = 0

    # Laske kaikkien muiden solujen arvot rekursiivisesti
    for i in range(1, n):
        for j in range(1, m):
            if seq1[i-1] == seq2[j-1]:
                LCS_matrix[i][j] = 1 + LCS_matrix[i-1][j-1]  # Lisää pisteitä vastaaville merkeille ja yhdistä aiemmat pituudet tämän solun arvoon.
