import tactic

def timsort(a: array nat) := do {
    l := len a;
    if l < 2 then return a;
    let mut piles := [];
    let mut run_start := 0;
    let mut run_length := 0;
    while run_start < l do {
        if run_length == 0 then {
            run_length := 1;
            run_start := run_start + 1;
            while run_start < l && a[run_start] == a[run_start - 1] do {
                run_length := run_length + 1;
                run_start := run_start + 1;
            };
        } else {
            let gap := run_length / 2;
            let i := run_start + gap;
            while i < l && a[i] == a[i - gap] do { i := i + gap };
            if i == l || a[i] != a[i - gap] then { piles.push (run_start, run_length); run_length := 0 }; else { run_length := i - run_start };
        };
        if piles.len == 0 then break;
        let (lo, hi) := piles.pop! (); hi := lo + hi; let (j, k) := partition lo hi; merge lo j k hi; piles.push (j, hi - j); piles.push (lo, j - lo); };
    mergeSort! piles.reverse! (); return a; } } ; timsort []

