def longest_common_substring(s1: str, s2: str) -> int:
    len_s1 = len(s1)
    len_s2 = len(s2)
    if len_s1 == 0 or len_s2 == 0:
        return 0
    dp = [[0 for _ in range(len_s2 + 1)] for _ in range(len_s1 + 1)]
    for i in range(len_s1):
        for j in range(len_s2):
            if s1[i] == s2[j]:
                if i == 0 or j == 0:
                    dp[i][j] = 1
                else:
                    dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = 0
    return max(dp[-1])
