def longest_common_subsequence(s1, s2):
    # 길이 0의 배열을 만든다.
    lcs = [[0] * (len(s2) + 1) for _ in range(len(s1) + 1)]
    for i in range(len(s1)):
        for j in range(len(s2)):
            if s1[i] == s2[j]:
                # 현재 문자와 일치하는 경우
                lcs[i + 1][j + 1] = lcs[i][j] + 1
            else:
                # 현재 문자가 일치하지 않는 경우
                lcs[i + 1][j + 1] = max(lcs[i + 1][j], lcs[i][j + 1])
    return lcs[-1][-1]
